mapper.select:
SELECT count(id) as count , DATE_FORMAT( t.create_time,'%Y%m%d' ) as date from
(select id,create_time FROM ${dto.tableName} u
WHERE DATE_FORMAT( u.create_time, '%Y%m%d' ) >= DATE_FORMAT( #{dto.startDate},'%Y%m%d')
and  DATE_FORMAT( u.create_time, '%Y%m%d' ) <= DATE_FORMAT( #{dto.endDate},'%Y%m%d')
) as t group by DATE_FORMAT( t.create_time, '%Y%m%d' ) ;
